void sieve(bool *c, int limit) {
    int i, p = 3, p2;

    c[0] = TRUE;
    c[1] = TRUE;

    for (;;) {
        p2 = p * p;
        if (p2 >= limit) {
            break;
        }
        for (i = p2; i < limit; i += 2*p) {
            c[i] = TRUE;
        }
        for (;;) {
            p += 2;
            if (!c[p]) {
                break;
            }
        }
    }
}

